' *-------------------------------------------------------',
' */',
'',
'-- Types to handle the GPS coordinates',
'-- Record for a particular position',
'type t_rec_gps_pos is record(',
'      name varchar2(200),   -- Name of the GPS (for tooltip)',
'      lat  varchar2(50),    -- Latitude',
'      lng  varchar2(50),    -- Longitute',
'      img  varchar2(1000)); -- Alternative marker icon e.g. #WORKSPACE_IMAGES#green_flag.jpg',
'  ',
'-- Collection to hold the GPS positions',
'type t_tab_gps_pos is table of t_rec_gps_pos index by binary_integer;',
'',
'/*-------------------------------------------------------',
' * Converts the base location for the alignment of the map (parameter 1)',
' * and the collection with all the marker positions (parameter 2)',
' * into a JSON string',
' *-------------------------------------------------------',
' */',
'function get_multimarker_json_string(pi_rec_gps_base   in t_rec_gps_pos,',
'                                     pi_tab_gps_marker in t_tab_gps_pos)',
'      return varchar2 is',
'      l_rec_gps t_rec_gps_pos;',
'      l_return  varchar2(32000);',
'      l_row     number := 0;',
'begin',
'      if pi_tab_gps_marker.first is null then',
'        return(null);',
'      end if;',
'    ',
'      l_return := ''{"base":['' || chr(10) || ''{'' || ''"name": "'' ||',
'                  pi_rec_gps_base.name || ''", '' || ''"lat": '' ||',
'                  pi_rec_gps_base.lat || '', '' || ''"lng": '' ||',
'                  pi_rec_gps_base.lng || ''}'' || '']},'' || chr(10);',
'    ',
'      for i in 1 .. pi_tab_gps_marker.count loop',
'        l_row := l_row + 1;',
'        if l_row = 1 then',
'          l_return := l_return || ''{"marker":['' || chr(10);',
'        else',
'          l_return := l_return || '','' || chr(10);',
'        end if;',
'        l_rec_gps := pi_tab_gps_marker(i);',
'        l_rec_gps.img := replace(l_rec_gps.img, chr(35) ||''WORKSPACE_IMAGES#'', v(''WORKSPACE_IMAGES''));',
'        l_rec_gps.img := replace(l_rec_gps.img, chr(35) ||''APPLICATION_IMAGES#'', v(''APPLICATION_IMAGES''));',
'      ',
'        l_return := l_return || ''{'' || ''"name": "'' || l_rec_gps.name ||',
'                    ''", "lat": '' || l_rec_gps.lat || '', "lng": '' ||',
'                    l_rec_gps.lng || '', "img": "'' || l_rec_gps.img || ''"'' || ''}'';',
'      ',
'      end loop;',
'    ',
'      if l_row > 0 then',
'        l_return := l_return || '']}'';',
'      end if;',
'    ',
'      l_return := ''{"gps":['' || chr(10) || l_return || chr(10) || '']}'';',
'    ',
'      return(l_return);',
'    ',
'end;',
'',
'/*-------------------------------------------------------',
' * MultiMarkerMap (Region Plugin) Callback Function for rendering',
' * Version: 1.0 (19.11.2014)',
' * Author:  S. Armbruster',
' * Requires: org.armbruster-it.apex.plugin.multimap.js',
' *-------------------------------------------------------',
' */',
'function render_multimap_region(p_region              in apex_plugin.t_region,',
'                                p_plugin              in apex_plugin.t_plugin,',
'                                p_is_printer_friendly in boolean)',
'  return apex_plugin.t_region_render_result IS',
'  l_html_string   varchar2(32767);',
'  l_map_width     number;',
'  l_map_height    number;',
'  l_map_zoom      number;',
'  l_map_id        varchar2(100);',
'  l_marker_icon   varchar2(1000);',
'  l_map_type      varchar2(100);',
'  l_debug         boolean;',
'  l_locations     varchar2(32000);',
'  l_result        APEX_PLUGIN.t_region_render_result;',
'  l_onload_string varchar2(32767);',
'  -- Variables for the dynamic SQL',
'  type t_cursor IS REF CURSOR;',
'  c_cursor  t_cursor;',
'  l_sql     varchar2(3000);',
'  l_rec_gps t_rec_gps_pos;',
'',
'  l_tab_gps t_tab_gps_pos;',
'  l_index   number := 0;',
'  -- to determine the center of the map',
'  l_lat_avg number := 0;',
'  l_lng_avg number := 0;',
'',
'begin',
'  l_map_width   := p_region.attribute_01;',
'  l_map_height  := p_region.attribute_02;',
'  l_map_zoom    := p_region.attribute_03;',
'  l_map_id      := p_region.attribute_04 || ''_'' || p_region.name;',
'  l_marker_icon := p_region.attribute_05;',
'  l_map_type    := p_region.attribute_06;',
'  l_debug       := nvl(p_region.attribute_07, ''N'') = ''Y'';',
'',
'  -- get the SQL out of the region source and create a cursor with it',
'  l_sql := p_region.source;',
'  open c_cursor for l_sql;',
'',
'  -- loop through all locations and store it in the collection',
'  loop',
'    fetch c_cursor',
'      into l_rec_gps;',
'    exit when c_cursor%NOTFOUND;',
'    ',
'    -- build the sum of the lat/lng to get the center of the map',
'    l_lat_avg := l_lat_avg + l_rec_gps.lat;',
'    l_lng_avg := l_lng_avg + l_rec_gps.lng;',
'  ',
'    l_index := l_index + 1;',
'    -- put each marker into the collection',
'    l_tab_gps(l_index) := l_rec_gps;',
'  ',
'  end loop;',
'',
'  -- if at least one record found, the compute the average for the alignment of the map',
'  if l_index > 0 then',
'    l_lat_avg := l_lat_avg / l_index;',
'    l_lng_avg := l_lng_avg / l_index;',
'  ',
'    -- put the average position into the first parameter for the alignment of the map (parameter 1) of',
'    -- the function get_multimarker_json_string',
'    l_rec_gps.lat  := l_lat_avg;',
'    l_rec_gps.lng  := l_lng_avg;',
'    l_rec_gps.name := ''XXX'';  -- name doesn''t matter for the align record',
'    ',
'    -- get the JSON string with the marker positions to pass it to the JS function',
'    l_locations := get_multimarker_json_string(l_rec_gps, l_tab_gps);',
'  ',
'  end if;',
'',
'  -- Close cursor:',
'  CLOSE c_cursor;',
'',
'  -- if the collection is empty, don''t show any map',
'  if (l_locations is null) then',
'    return(l_result);',
'  end if;',
'',
'  -- Include Google Library ',
'  APEX_JAVASCRIPT.add_library(p_name           => ''https://maps.googleapis.com/maps/api/js?v=3.exp'',',
'                              p_directory      => NULL,',
'                              p_version        => NULL,',
'                              p_skip_extension => TRUE);',
'',
'  -- Include the Core JS Library',
'  APEX_JAVASCRIPT.add_library(p_name           => ''itstar.core'',',
'                              p_directory      => p_plugin.file_prefix,',
'                              p_version        => NULL,',
'                              p_skip_extension => FALSE);',
'',
'  -- Include the multi-marker-map JS Library',
'  APEX_JAVASCRIPT.add_library(p_name           => ''itstar.apex.plugins.googleMap'',',
'                              p_directory      => p_plugin.file_prefix,',
'                              p_version        => NULL,',
'                              p_skip_extension => FALSE);',
'',
'  --',
'  -- in Debug-Mode show the JSON String ',
'  if l_debug then',
' ',
'    l_html_string := ''<textarea id="'' || p_region.name ||',
'                     ''" class="textarea" wrap="virtual" maxlength="50" cols="50" rows="5">'' ||',
'                     htf.escape_sc(l_locations) || ''</textarea>'';',
'    sys.htp.prn(l_html_string);',
'  ',
'  end if;',
'  --',
'  l_html_string := ''<div style="width:'' || l_map_width || ''px;height:'' ||',
'                   l_map_height || ''px;" id="'' || l_map_id || ''"> </div>'';',
'  SYS.HTP.PRN(l_html_string);',
'',
'  l_onload_string := ''itstar.apex.plugins.googleMap.displayMap("'' || p_region.name || ''", '' || ''{'' ||',
'                     APEX_JAVASCRIPT.add_attribute(p_name      => ''map_width'',',
'                                                   p_value     => l_map_width,',
'                                                   p_add_comma => TRUE) ||',
'                     APEX_JAVASCRIPT.add_attribute(p_name      => ''map_height'',',
'                                                   p_value     => l_map_height,',
'                                                   p_add_comma => TRUE) ||',
'                     APEX_JAVASCRIPT.add_attribute(p_name      => ''map_zoom'',',
'                                                   p_value     => l_map_zoom,',
'                                                   p_add_comma => TRUE) ||',
'                     APEX_JAVASCRIPT.add_attribute(p_name      => ''map_id'',',
'                                                   p_value     => l_map_id,',
'                                                   p_add_comma => TRUE) ||',
'                     APEX_JAVASCRIPT.add_attribute(p_name      => ''map_locations'',',
'                                                   p_value     => l_locations,',
'                                                   p_add_comma => TRUE) ||',
'                     APEX_JAVASCRIPT.add_attribute(p_name      => ''marker_icon'',',
'                                                   p_value     => l_marker_icon,',
'                                                   p_add_comma => TRUE) ||',
'                     APEX_JAVASCRIPT.add_attribute(p_name      => ''map_type'',',
'                                                   p_value     => l_map_type,',
'                                                   p_add_comma => TRUE) ||',
'                     ''});'';',
'',
'  --',
'  apex_javascript.add_onload_code(p_code => l_onload_string);',
'  --',
'',
'  return(l_result);',
'',
'end;',
'',
