,p_plsql_code=>wwv_flow_utilities.join(wwv_flow_t_varchar2(
'  function render_s3slider_ajax (',
'      p_region              in apex_plugin.t_region,',
'      p_plugin              in apex_plugin.t_plugin',
') return apex_plugin.t_region_ajax_result',
'  is',
'    /* Funktion um den Inhalt zu generieren */',
'      ',
'    v_datafound boolean := false;',
'    v_nodatamsg varchar2(50) := ''No data found.'';',
'',
'    v_sql         varchar2(32767);',
'    v_new_sql     varchar2(32767) := ''''; ',
'    v_sql_handler apex_plugin_util.t_sql_handler;',
'    v_dtlist      wwv_flow_global.vc_arr2;',
'    v_sql_values  apex_plugin_util.t_column_value_list;',
'    ',
'    v_id          varchar2(50);',
'    v_img         varchar2(500);',
'    v_text        varchar2(500);',
'    v_description varchar2(2000);',
'    v_position    varchar2(10); -- left, top, right, bottom',
'  begin',
'    /* Spaltentypen:  ID, IMG, TEXT, DESCRIPTION, POSITION (Left, Top, Bottom, Right) */',
'    v_dtlist(1) := apex_plugin_util.c_data_type_number;',
'    v_dtlist(2) := apex_plugin_util.c_data_type_varchar2;',
'    v_dtlist(3) := apex_plugin_util.c_data_type_varchar2;',
'    v_dtlist(4) := apex_plugin_util.c_data_type_varchar2;',
'    v_dtlist(5) := apex_plugin_util.c_data_type_varchar2;',
'',
'',
'    /* SQL Handler konfigurieren */',
'    v_sql := p_region.source;',
'    ',
'    v_sql_handler := apex_plugin_util.get_sql_handler(',
'      p_sql_statement => v_sql,',
'      p_min_columns   => 5,',
'      p_max_columns   => 5,',
'      p_data_type_list => v_dtlist,',
'      p_component_name => null',
'    );',
'    ',
'    /* SQL Handler ausfuehren / Daten laden */',
'    if wwv_flow.g_debug then ',
'      wwv_flow.debug(''get_sql_handler called for query: ''||v_sql);',
'    end if;',
'',
'    apex_plugin_util.prepare_query(',
'      p_sql_handler   => v_sql_handler',
'    );',
'',
'    if wwv_flow.g_debug then ',
'      wwv_flow.debug(''prepare_query called.'');',
'    end if;',
'   ',
'    v_sql_values := apex_plugin_util.get_data(',
'      p_sql_handler   => v_sql_handler',
'    );',
'',
'    if wwv_flow.g_debug then ',
'      wwv_flow.debug(''get_data called.'');',
'    end if;',
'    ',
'    apex_plugin_util.free_sql_handler(',
'      p_sql_handler   => v_sql_handler',
'    );',
'',
'    if wwv_flow.g_debug then ',
'      wwv_flow.debug(''free_handler called.'');',
'    end if;',
'    ',
'    /* NoDataFound Check */',
'    begin',
'      v_datafound := true;',
'      if wwv_flow.g_debug then ',
'        wwv_flow.debug(v_sql_values(1).count || '' row(s) returned.'');',
'      end if;',
'    exception',
'      when NO_DATA_FOUND then ',
'       v_datafound := false;',
'       if wwv_flow.g_debug then ',
'         wwv_flow.debug(''no rows returned.'');',
'       end if;',
'    end;',
'    if v_datafound then',
'      /* ul Startelement ausgeben */',
'      sys.htp.p(''<ul id="''||apex_plugin_util.escape(p_region.static_id, true)||''_sliderContent">'');',
'    ',
'      /* Parameter auslesen */',
'      for i in 1..v_sql_values(1).count loop',
'        v_id := to_char(v_sql_values(1)(i));',
'        v_img := v_sql_values(2)(i);',
'        v_text := v_sql_values(3)(i);',
'        v_description := v_sql_values(4)(i);',
'        v_position := v_sql_values(5)(i);',
'        if v_position != ''left'' and v_position != ''top'' and v_position != ''right'' and v_position != ''bottom'' then',
'          v_position := ''left'';',
'        end if;',
'        /* Inhalt ausgeben */',
'        sys.htp.p(''<li class=''''''||apex_plugin_util.escape(p_region.static_id, true)||''_sliderImage''''>'');',
'        sys.htp.p(''<img src=''''''||v_img||''''''>'');',
'        sys.htp.p(''<span class=''''''||v_position||''''''><strong style=''''font-size:120%;''''>''||v_text||''</strong><br>''||v_description||''</span>'');',
'        sys.htp.p(''</li>'');',
'      end loop;',
'	    /* Elemente schliessen */',
'        sys.htp.p(''<div class=''''clear ''||apex_plugin_util.escape(p_region.static_id, true)||''_sliderImage''''></div>'');',
'        sys.htp.p(''</ul>'');',
'        ',
'      if wwv_flow.g_debug then ',
'        wwv_flow.debug(''source rendered.'');',
'      end if;',
'    else',
'      sys.htp.p(v_nodatamsg);',
'      if wwv_flow.g_debug then ',
'        wwv_flow.debug(''no data found message rendered.'');',
'      end if;',
'    end if;',
'    return null;',
'  exception ',
'    when others then ',
'      begin',
'        apex_plugin_util.free_sql_handler(',
'          p_sql_handler   => v_sql_handler',
'        ); ',
'      exception when others then null;',
'      end;',
'      sys.htp.p(''An exception occurred - turn on debug mode to see more information.<br/>'');',
'      if wwv_flow.g_debug then ',
'        sys.htp.p(''<pre>''||dbms_utility.format_error_stack||''</pre>'');',
'      end if;',
'      wwv_flow.debug(sqlerrm);',
'      wwv_flow.debug(dbms_utility.format_error_stack);',
'      return null;',
'  end render_s3slider_ajax; ',
'  ',
'/* ------------------------------------------------------------------------------------------- */',
'',
