'    l_opacity        apex_application_page_regions.attribute_06%type := p_region.attribute_06;',
'    l_items_submit   apex_application_page_regions.attribute_07%type := p_region.attribute_07;',
'    l_ajax_result    apex_plugin.t_region_ajax_result;',
'begin',
'  apex_javascript.add_library (',
'    p_name      => ''s3slider'',',
'    p_directory => p_plugin.file_prefix,',
'    p_version   => null ',
'  );',
'  apex_javascript.add_library (',
'    p_name      => ''aaw_s3slider'',',
'    p_directory => p_plugin.file_prefix,',
'    p_version   => null ',
'  );',
'  /* Javsascript Aufruf der Slider Funktion generieren */',
'  sys.htp.p(''<script type=''''text/javascript''''>'');',
'  sys.htp.p(''function '' || apex_plugin_util.escape(p_region.static_id, true) || ''_run_slider () {$(''''#''||apex_plugin_util.escape(p_region.static_id, true)||''_slider'''').s3Slider({timeOut: '' || to_char(l_timeout) || '' });}'');',
'  sys.htp.p(''$(document).ready(function() {'' || apex_plugin_util.escape(p_region.static_id, true) || ''_run_slider ();});'');',
'  sys.htp.p(''</script>'');',
'  if wwv_flow.g_debug then ',
'     wwv_flow.debug(''s3slider javascript added.'');',
'  end if;',
'',
'    /* CSS Generieren */',
'    sys.htp.p(''<style rel="stylesheet" type="text/css">'');',
'    sys.htp.p(''#''||apex_plugin_util.escape(p_region.static_id, true)||''_slider{''||',
'        ''width: '' || to_char(l_width) || ''px; /* important to be same as image width */  '' ||',
'        ''height: '' || to_char(l_height) || ''px; /* important to be same as image height */  '' ||',
'        ''position: relative; /* important */  '' ||',
'        ''overflow: hidden; /* important */  '' ||',
