'    sys.htp.p(''#''||apex_plugin_util.escape(p_region.static_id, true)||''_sliderContent{width: 800px;position: absolute;top: 0;margin-left: 0;}'');',
'    sys.htp.p(''.''||apex_plugin_util.escape(p_region.static_id, true)||''_sliderImage{float: left;position: relative;display: none;}'');',
'    sys.htp.p(''.''||apex_plugin_util.escape(p_region.static_id, true)||''_sliderImage span{ '' ||',
'        ''position: absolute;  '' ||',
'        ''font: 11px/15px Arial, Helvetica, sans-serif;  '' ||',
