'        ''color: '' || l_color || ''; '' ||',
'        ''display: none; }'');',
'    sys.htp.p(''.clear {clear: both;}'');',
'    sys.htp.p(''.''||apex_plugin_util.escape(p_region.static_id, true)||''_sliderImage span strong {font-size: 14px;}'');',
'    sys.htp.p(''.top {top: 0;left: 0;}'');',
'    sys.htp.p(''.bottom {bottom: 0;left: 0;}'');',
'    sys.htp.p(''.left {top: 0;left: 0;width: 120px !important;height: 280px;}'');',
'    sys.htp.p(''.right {right: 0;bottom: 0;width: 90px !important;height: 280px;}'');',
'    sys.htp.p(''</style>'');',
'  if wwv_flow.g_debug then ',
'     wwv_flow.debug(''s3slider css added.'');',
'  end if;',
'  ',
'  /* Basis Element */',
'  sys.htp.p(',
'    ''<div id="''||apex_plugin_util.escape(p_region.static_id, true)||''_slider">'');',
'    l_ajax_result :=  render_s3slider_ajax(p_region, p_plugin);',
