'    end if;',
'  end if;',
'  ',
'  sample_pkg.demo_tag_sync(',
'    p_new_tags      => :new.tags,',
'    p_old_tags      => :old.tags,',
'    p_content_type  => ''ORDER'',',
'    p_content_id    => :new.order_id );',
'END;',
'/',
'',
'create or replace trigger "DEMO_ORDERS_BD"',
